package com.axp.dao;import java.util.List;import com.axp.model.ReGoodsOfSellerMall;import com.axp.model.Seller;import com.axp.query.PageResult;public interface ReGoodsOfSellerMallDAO extends IBaseDao<ReGoodsOfSellerMall> {    /**     * 查询商城商品列表；     *     * @param seller      商家；     * @param currentPage 当前页；     * @param pageSize    每页显示条数；     * @param searchWord  模糊搜索词；     * @return     */    PageResult<ReGoodsOfSellerMall> getGoodsList(Seller seller, Integer currentPage, Integer pageSize, String searchWord, Integer goodsType);    /**     * 查找出当前最大的置顶版本号     *     * @param sellerId     * @return     */    int findMasStickyNum(Integer sellerId);    /**     * 获取所有的待审核的商家商城的商品；     *     * @param currentPage 当前页；     * @param pageSize    每页条数；     * @return     */    PageResult<ReGoodsOfSellerMall> getCheckPageresult(Integer currentPage, Integer pageSize,Integer adminUserId);    /**     * 根据基础商品id获取商品；     *     * @param id     * @return     */    List<ReGoodsOfSellerMall> getGoodsByBaseGoodsId(Integer id);        /**     * 根据周边商品id判断该商品是否重复推广；     *     * @param sellerMallId     * @return     */    List<Object[]> findPutStatus(Integer sellerMallId);}