package com.axp.dao;import java.util.List;import java.util.Set;import com.axp.model.RePermission;public interface RePermissionDAO extends IBaseDao<RePermission> {    /**     * 获取所有的权限；     * 不包括权限的分类；     *     * @return     */    List<RePermission> getAllPermissionsOfNeed();    /**     * 从数据库中获取给定集合中的权限对象，和他们的父级对象；     * 须知：     * 在角色上传时，上传的都是权限的id值，要返回这些id值；     * 同时还要返回这些id值对应的父级权限的对象；     *     * @param rePermissions     * @return     */    Set<RePermission> getPermissionByIdList(List<Integer> rePermissions);}