package com.axp.dao;import java.util.List;import com.axp.model.AdminUser;import com.axp.model.RePermissionRole;import com.axp.query.PageResult;public interface RePermissionRoleDAO extends IBaseDao<RePermissionRole> {    /**     * 获取给定的用户的给定页的角色对象们；     *     * @param adminUserId    后台用户id；     * @param currentPage    当前页；     * @param pageSize       每页条数；     * @param permissionName 权限名称（搜索用）     * @return     */    PageResult<RePermissionRole> getRoleList(Integer adminUserId, Integer currentPage, Integer pageSize, String permissionName);    /**     * 根据用户集合，找出这些用户创建的角色；     *     * @param roleUser     * @return     */    List<RePermissionRole> getRolesByAdminList(List<AdminUser> roleUser);}