package com.axp.dao.impl;import java.util.List;import org.hibernate.Query;import org.hibernate.Session;import org.springframework.stereotype.Repository;import com.axp.dao.CaptchaDAO;import com.axp.model.Captcha;@Repositorypublic class CaptchaDAOImpl extends BaseDaoImpl<Captcha> implements CaptchaDAO {    @Override    public Boolean contaionCaptcha(String phoneNumber, String securityCode) {        Session session = sessionFactory.getCurrentSession();        String hql = "from Captcha where isValid=1 and phone=:phoneNumber and code=:securityCode";        Query query = session.createQuery(hql);        List<Captcha> list = query.setParameter("phoneNumber", phoneNumber).setParameter("securityCode", securityCode).list();        if (list != null && list.size() == 1) {            Captcha captcha = list.get(0);            captcha.setIsvalid(false);            return true;        }        return false;    }}