package com.axp.dao.impl;import java.util.List;import org.hibernate.Query;import org.hibernate.Session;import org.springframework.stereotype.Repository;import com.axp.dao.IReGoodsStandardDao;import com.axp.model.ReGoodsStandard;@SuppressWarnings("unchecked")@Repository public class ReGoodsStandardDaoImpl extends BaseDaoImpl<ReGoodsStandard> implements IReGoodsStandardDao {		@Override	public List<ReGoodsStandard> getParentStandardList(Integer adminUserId){		String queryString = "from ReGoodsStandard where adminUserId = :adminUserId and parentStandardId is null";		Session session = sessionFactory.getCurrentSession();		Query queryObject = session.createQuery(queryString);		queryObject.setParameter("adminUserId", adminUserId);		return queryObject.list();	}	@Override	public void deleteStandartByParent(Integer id) {		String queryString = "update ReGoodsStandard set isValid = false where parentStandardId = "+ id;		Session session = sessionFactory.getCurrentSession();		Query queryObject = session.createQuery(queryString);		queryObject.executeUpdate();					}	}