package com.axp.dao.impl;import java.util.HashSet;import java.util.List;import java.util.Set;import org.hibernate.Query;import org.hibernate.Session;import org.springframework.stereotype.Repository;import com.axp.dao.ReItemDAO;import com.axp.model.ReItem;@Repositorypublic class ReItemDAOImpl extends BaseDaoImpl<ReItem> implements ReItemDAO {    @Override    public List<ReItem> getAllItems() {        Session session = sessionFactory.getCurrentSession();        Query query = session.createQuery("from ReItem where isValid=1");        return query.list();    }        @Override    public Set<ReItem> getItemsByIds(List<Integer> ids) {    	 Session session = sessionFactory.getCurrentSession();         Query query = session.createQuery("from ReItem where id in :set and isValid=1").setParameterList("set", ids);         List<ReItem> list = query.list();         Set<ReItem> returnSet = new HashSet<>();         returnSet.addAll(list);         return returnSet;    }}