package com.axp.dao.impl;import org.hibernate.Session;import org.springframework.stereotype.Repository;import com.axp.dao.SystemConfigDAO;import com.axp.model.SystemConfig;import java.util.List;@Repositorypublic class SystemConfigDAOImpl extends BaseDaoImpl<SystemConfig> implements SystemConfigDAO {    @Override    public SystemConfig getConfigByParameter(String parameter) {        Session session = sessionFactory.getCurrentSession();        List<SystemConfig> list = session.createQuery("from SystemConfig where parameter=:parameter").setParameter("parameter", parameter).list();        return list == null || list.size() == 0 ? null : list.get(0);    }}