package com.axp.service.goods;import javax.servlet.http.HttpServletRequest;import com.axp.model.ReBaseGoods;public interface ReBaseGoodsService {    /**     * 将回显的数据放到session中；     *     * @param request          无返回数据；     * @param isForShow        只是为了展示用，将所有输入框变成展示，并且不可删除；     * @param isEditWithoutDel 补充库存时使用，没有删除按钮；     * @param baseGoodsId      基础商品的id值；     */    void returnBack(HttpServletRequest request, Integer baseGoodsId, Boolean isForShow, Boolean isEditWithoutDel);    void edit(Integer id, HttpServletRequest request);    /**     * 根据商城类型id值，和商品的id值，获取这个商品对象，使用它们共同的父类（ReBaseGoods）接收这个对象     *     * @param mallType     * @param goodsId     * @return     */    ReBaseGoods getGoodsByMallTypeAndGoodsId(Integer mallType, Integer goodsId) throws Exception;    /**     * 增加库存操作；     *     * @param isNoStandard 是否有商品规格；     * @param reBaseGoods     * @param request     */    void addRepertory(Boolean isNoStandard, ReBaseGoods reBaseGoods, HttpServletRequest request) throws Exception;	/**	 * 	 * 主要处理LockMall商城的	 * @param request	 * @param baseGoodsId	 * @param b	 * @param c	 * @param goodsOrder	 */	void returnBack_lock(HttpServletRequest request, Integer baseGoodsId, Boolean isForShow, Boolean isEditWithoutDel, String goodsOrder);}