package com.axp.service.money;import java.util.Map;import com.axp.model.ReSellerWithdrawData;import com.axp.model.Seller;import com.axp.query.PageResult;public interface ReSellerWithdrawDataService {    /**     * 保存商家提现信息；     *     * @param seller     * @param data     * @param returnMap     * @return     */    Map<String, Object> doSave(Seller seller, ReSellerWithdrawData data, Map<String, Object> returnMap);    /**     * 查询出需要展示的商家提现资料列表；     *     * @param currentPage     * @param pageSize     * @return     */    PageResult<ReSellerWithdrawData> getCheckDataList(Integer currentPage, Integer pageSize);    /**     * 根据id寻找对象；     *     * @param withdrawDataId     * @return     */    ReSellerWithdrawData findById(Integer withdrawDataId);    /**     * 商家提现信息审核操作；     *     * @param returnMap   返回值；     * @param id          审核的商家提现信息id值；     * @param checkStatus 审核状态；     * @param checkInfo   审核信息；     * @return     */    Map<String, Object> doCheck(Map<String, Object> returnMap, Integer id, Integer checkStatus, String checkInfo) throws Exception;}