package com.axp.service.system.impl;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.springframework.stereotype.Service;import com.axp.model.AdminuserWithdrawals;import com.axp.model.GetmoneyRecord;import com.axp.model.ReBackOrder;import com.axp.service.system.DataCenterService;import com.axp.util.QueryModel;@Servicepublic class DataCenterServiceImpl extends BaseServiceImpl implements DataCenterService {	/*	 * @未支付退单	 * @Author:zl	 * */	@Override	public Integer getBackOrderNumber(HttpServletRequest request,			HttpServletResponse response) {		QueryModel model=new QueryModel();		model.combPreEquals("backState", ReBackOrder.BACKSTATE_yishenhe);		model.combPreEquals("isValid", true);		return dateBaseDao.findCount(ReBackOrder.class, model);	}	/*	 * @未支付粉丝提现	 * @Author:zl	 * */	@Override	public Integer getFansWithdrawReviewNumber(			HttpServletRequest request, HttpServletResponse response) {		QueryModel model=new QueryModel();		model.combPreEquals("status", GetmoneyRecord.wei_zhi_fu);		model.combPreEquals("isValid", true);		return dateBaseDao.findCount(GetmoneyRecord.class, model);	}	/*	 * @未支付商家提现	 * @Author:zl	 * */	@Override	public Integer getSellerWithdrawReviewNumber(			HttpServletRequest request, HttpServletResponse response) {		QueryModel model=new QueryModel();		model.combPreEquals("state", AdminuserWithdrawals.shen_qing);		model.combPreEquals("isValid", true);		return dateBaseDao.findCount(AdminuserWithdrawals.class, model);	}		}